END_IF
IF arErrorStan[enumPPPSKlimatyzatorTroxSchoolairVError.BrakKomunikacji] <> enumAlarmWartosc.StanNormalny AND arModbusStatusKomunikacji[bAdres] = enumModbusStatus.KomunikacjaPrawidlowa THEN
