				FOR intPetla := 1 TO 10 BY 1 DO
					// Raportowanie typu E (stanów alarmów) na zapytanie typu Q
					IF arErrorStan[intPetla] <> enumAlarmWartosc.StanNieznany THEN
						fnZapiszKomunikat(	sCalyKomunikat := '' ,
