					END_IF
				END_FOR
			END_IF
		END_IF
		// Raportowanie typu A na zapytanie Q
		IF sDane <> '' THEN
			fnZapiszKomunikat(	sCalyKomunikat := '' ,
								sTypKomunikatu := 'A' ,
								sMnemonik := sMnemonik ,
								sMACAdres := sMACAdres ,
