		END_IF	
	END_IF
	// Biezaca moc licznika
	IF rMoc <> rMocZaraportowana THEN
		rMocZaraportowana := rMoc;
		fnKomunikatDaneDodaj_xOverFlow(sDane := sDane , sAtrybut := 'POWCC=' , sWartosc := REAL_TO_STRING(rMoc) , xOverFlow := xOverFlow);	
